import React, { useState } from "react";
import PropTypes from "prop-types";
import Link from "next/link";
import { Menu } from "antd";

const menuItems = [
  {
    label: <Link to="/">홈</Link>,
    key: "home",
  },
  {
    label: <Link to="/profile">프로필</Link>,
    key: "profile",
  },
  {
    label: <Link to="/signup">회원가입</Link>,
    key: "signup",
  },
];

const AppLayout = ({ children }) => {
  const [current, setCurrent] = useState("home");
  const onMenu = (e) => {
    setCurrent(e.key);
  };
  return (
    <div>
      {/* <Menu mode="horizontal">
        <Menu.Item key="home">
          <Link href="/">
            <a>노드버드</a>
          </Link>
        </Menu.Item>
        <Menu.Item key="profile">
          <Link href="/profile">
            <a>프로필</a>
          </Link>
        </Menu.Item>
        <Menu.Item key="signup">
          <Link href="/signup">
            <a>회원가입</a>
          </Link>
        </Menu.Item>
      </Menu> */}
      <Menu
        onClick={onMenu}
        selectedKeys={[current]}
        items={menuItems}
        mode="horizontal"
      />
      <div>{children}</div>
    </div>
  );
};

AppLayout.propTypes = {
  children: PropTypes.node.isRequired,
};

export default AppLayout;
